<?php$source = __DIR__ . '/source/Thalion-v1.05-DE/Amberfiles';$target = __DIR__ . '/infoset/Amberfiles';$iterator = new RecursiveIteratorIterator(new RecursiveDirectoryIterator($source));$xml = <<<EOT<?xml version="1.0" encoding="UTF-8"?><archive xmlns="http://schema.slothsoft.net/savegame/editor"	path="%s"	type="?">	</archive>EOT;foreach ($iterator as $sourceFile) {	$amberFile = str_replace($source, '', (string) $sourceFile);	$targetFile = "$target$amberFile.xml";	echo $targetFile . PHP_EOL;	$targetXml = sprintf($xml, str_replace('\\', '/', 'Amberfiles' . $amberFile));		if (!is_dir(dirname($targetFile))) {		mkdir(dirname($targetFile), 0777, true);	}	file_put_contents($targetFile, $targetXml);}