head.script:
const targets = document.querySelectorAll("article");
for (const target of targets) {
    for (const template of document.querySelectorAll("template")) {
        const text = template.content.textContent.trim();
        const p = document.createElementNS(target.namespaceURI, "p");
        p.setAttribute("class", "text");
        for (let i = 0; i < text.length; i++) {
            const t = text[i];
            if (t == "\n") {
                p.appendChild(document.createElementNS(target.namespaceURI, "br"));
            } else {
                const span = document.createElementNS(target.namespaceURI, "span");
                span.textContent = t;
                p.appendChild(span);
            }
        }
        target.appendChild(p);
    }
}
